import React from 'react';
import { Bar } from 'react-chartjs-2';
import './TriggerAnalysis.css';

const TriggerAnalysis = () => {
  // 각 요일별로 발생한 트리거 횟수 합산
  const triggerData = { // 예시
    '월': ['폭식', '구토', '변비약 복용', '씹고 뱉기'],
    '화': ['구토'],
    '수': ['폭식', '구토', '씹고 뱉기'],
    '목': ['폭식', '변비약 복용'],
    '금': ['폭식', '변비약 복용', '씹고 뱉기'],
    '토': ['구토', '씹고 뱉기'], 
    '일': ['폭식', '구토', '씹고 뱉기'], 
  };

  const totalTriggersPerDay = Object.keys(triggerData).map(day => triggerData[day].length);

  const barData = {
    labels: ['월', '화', '수', '목', '금', '토', '일'],
    datasets: [
      {
        label: '트리거 발생 횟수',
        data: totalTriggersPerDay,
        backgroundColor: '#FFCF24',
      },
    ],
  };

  const symptomData = {
    labels: ['폭식', '구토', '변비약 복용', '씹고 뱉기'],
    datasets: [
      {
        data: [4, 3, 2, 1],
        backgroundColor: ['#FFCF24', '#FFDF24', '#FFE589', '#FFD700'],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="trigger-analysis">
      <h2>식사 트리거 증상 분석</h2>
      <h3>주간 트리거 발생</h3>
      <div className="chart-container">
        <Bar data={barData} options={{ plugins: { legend: { display: false } } }} />
      </div>
      <div className="symptom-container">
        <div className="symptom-title">증상별 발생 빈도</div>
        <div className="symptom-bar">
          {symptomData.labels.map((label, index) => (
            <div key={index} className="symptom-segment" style={{ backgroundColor: symptomData.datasets[0].backgroundColor[index], flexGrow: symptomData.datasets[0].data[index] }}>
              <span className="symptom-label">{label}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TriggerAnalysis;
