import React, { useState } from 'react';
import './Settings.css';
import ic_back from '../assets/icon-back.png';

const Settings = () => {
  const [showPopup, setShowPopup] = useState(false);

  const handleDeleteAccount = () => {
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  const handleConfirmDelete = () => {
    // 여기에 계정 삭제 로직을 추가하세요
    alert('계정이 삭제되었습니다.');
    setShowPopup(false);
  };

  return (
    <div className='settings-container'>
      <header className='settings-header'>
        <img src={ic_back} alt='back' className='back-icon' onClick={() => window.history.back()} />
        <div className='settings-title'>설정</div>
      </header>
      <div className='settings-content'>
        <div className='settings-password'>비밀번호 설정</div>
        <div className='settings-logout'>로그아웃</div>
        <div className='settings-delete' onClick={handleDeleteAccount}>계정 삭제하기</div>
      </div>
      {showPopup && (
        <div className='popup'>
          <div className='popup-inner'>
            <p>계정 삭제하기</p>
            <p>정말로 계정을 삭제하시겠습니까? </p>
            <p>계정 복구는 불가능합니다.</p>
            <button onClick={handleClosePopup}>취소</button>
            <button onClick={handleConfirmDelete}>삭제</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Settings;
