// src/components/WeeklyReport.js
import React from 'react';
import { Pie } from 'react-chartjs-2';
import './WeeklyReport.css';

const WeeklyReport = () => {
  const data = {
    labels: ['점심', '저녁', '야식'],
    datasets: [
      {
        data: [20, 40, 40], // 임의로 데이터 입력
        backgroundColor: ['#FFCF24', '#696A73', '#FFE589'],
        borderWidth: 0, // 차트 사이에 있는 흰색 구분선 제거
      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        display: true,
        position: 'right',
        labels: {
          generateLabels: (chart) => {
            const datasets = chart.data.datasets;
            return chart.data.labels.map((label, i) => {
              return {
                text: label,
                fillStyle: datasets[0].backgroundColor[i],
                strokeStyle: datasets[0].backgroundColor[i],
                lineWidth: 0,
                hidden: false,
                index: i,
                pointStyle: 'circle',
                radius: 10, // 범례 원형 아이콘의 크기 조절
              };
            });
          },
          usePointStyle: true,
        },
      },
    },
  };

  return (
    <div className='weekly-report'>
      <h2>주간 리포트 <span className='report-date'>(7.22-7.28)</span></h2>
      <div className='chart-container'>
        <Pie data={data} options={options} />
      </div>
      <div className='report-message'>규칙적으로 세끼 식사를 하는게 좋아요!</div>
    </div>
  );
};

export default WeeklyReport;
