import React, { useState } from 'react';
import './PraiseMyself.css';

const guide = [
  "오늘 스타일에 바뀐 점이 있나요?",
  "바뀐 점들 중 어떤 점이 가장 마음에 드나요?",
  "그 점을 이용하여 나에게 칭찬을 남겨주세요."
];

const keywords = ['살', '마른'];

const PraiseMyself = () => {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState(["", "", ""]);

  const handleNext = () => {
    if (step < guide.length - 1) {
      setStep(step + 1);
    } else {
      setStep(guide.length);
    }
  };

  const handleChange = (e) => {
    const newAnswers = [...answers];
    newAnswers[step] = e.target.value;
    setAnswers(newAnswers);
  };

  const highlightKeywords = (text) => {
    return text.split(' ').map((word, index) => (
      keywords.includes(word) ?
      <span key={index} style={{ color: 'yellow' }}>{word} </span> :
      <span key={index}>{word} </span>
    ));
  };

  return (
    <div className="praisemyself-container">
      {step < guide.length ? (
        <>
          <div className="guide">{guide[step]}</div>
          <input
            type="text"
            placeholder="내 답변 입력"
            value={answers[step]}
            onChange={handleChange}
            className="answer-input"
          />
          <button onClick={handleNext} className="next-button">
            {step < guide.length - 1 ? "다음으로" : "오늘의 칭찬 완료"}
          </button>
        </>
      ) : (
        <div className="summary">
          <div className="summary-title">오늘 나에게 칭찬한 점이에요!</div>
          <div className="answers">
            {answers.map((answer, index) => (
              <p key={index} className="answer-summary">{highlightKeywords(answer)}</p>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PraiseMyself;
